var veicoloFactory = {
	creaVeicolo: function(opzioni) {
		var veicolo;
		if (opzioni && opzioni.length) {
			switch (opzioni.tipo) {
				case "auto":
					veicolo = new Automobile(opzioni);
					break;
				case "moto":
					veicolo = new Moto(opzioni);
					break;
				case "camion":
					veicolo = new Camion(opzioni);
					break;
			}
		}
	}
};

module.exports.veicoloFactory = veicoloFactory;